  {
    let result = window.confirm("Are you sure to delete?");
    if(result)
    {
       let newtasks = alltasks.filter((todo) => todo.id !== id);
       setalltasks([...newtasks]);
    }
  };
  const doneTodo = (e,id )=> {
  if (e.currentTarget.style.backgroundColor) {
    e.currentTarget.style.backgroundColor = null;
