    alltasks=alltasks.map(obj =>{if (obj.id === id ) {return {...obj, done: false};}return obj;});
    window.localStorage.setItem("tasks", (JSON.stringify(alltasks))); 
  } else {
    e.currentTarget.style.backgroundColor = 'green';
    e.currentTarget.style.color = 'white';
    alltasks=alltasks.map(obj =>{if (obj.id === id ) {return {...obj, done: true};}return obj;});
   window.localStorage.setItem("tasks", (JSON.stringify(alltasks))); 
  }
  };

  let ListItems = Readtasks.map((todo) => {
    return(
        <div key ={todo.id} >
          <p>{todo.task}</p>
          <button className="done btn_class"  onClick={(e) => doneTodo(e,todo.id)}>Done </button>
          <button className='delete' onClick={() => deleteTodo(todo.id)}>Delete</button>
          <p>{todo.assignee}</p>
        </div>
    )
 });

      return (
        <div>
          <form id="form">
            <label htmlFor="taskname">Task Name: 
            <input  required name="taskname" id="taskname" value={task} onChange={(e)=>(settask(e.target.value))} type="text" placeholder="task name" />
                </label>
                <label htmlFor="assignee">Assignee : 
            <input  required name="assignee" id="assignee" value= {assignee} onChange={(e)=>(setassignee(e.target.value))} type="text" placeholder="assignee" />
            <button className="b_add" onClick={savedata} >Add Task</button>
                </label>
            </form>
          <hr/>
          <div className="search">
        <input
          type="text"
          value={searchText}
