import React from 'react';
function Search({alltasks})
{
    const [searchText, setSearchText] = React.useState("");
    const filteredtasks = alltasks.filter(
        ({ task, assignee }) =>
          task.toLowerCase().includes(searchText.toLowerCase()) ||
          assignee.toLowerCase().includes(searchText.toLowerCase())
      );
      
  
  let ListItems=filteredtasks.map((todo) => {
    return(
        <div key ={todo.id} >
          <p>{todo.task}</p>
          <button className="done"  onClick={() => doneTodo(todo.id)}>Done </button>
          <button className='delete' onClick={() => deleteTodo(todo.id)}>Delete</button>
          <p>{todo.assignee}</p>
        </div>
    )
 });
    return(
        <div>
        <section class="search">
        <input
          type="text"
          value={searchText}
          id="search" placeholder=" Search"
          onChange={({ target }) => setSearchText(target.value)}
        />
        </section>
          <section className="my-tasks">
          {ListItems}
          </section></div>
    );
}
export default Search;
