          onChange={({ target }) => setSearchText(target.value)}
        />
         <ul>
        {filteredtasks.map(({ assignee, task }) => (
          <li key={task}>
            <strong>{task}</strong> by {assignee}
          </li>
        ))}
      </ul>
    </section>
    );
}
