import React , {useState} from 'react';
import Search from './Search';
import End from './End';
//import {ButtonComponent} from '@syncfusion/ej2-react-buttons';

 import './index.css';

 function App ()
 {
  const[task,settask] = useState('')
  const[assignee,setassignee] = useState('')

  let alltasks = JSON.parse(localStorage.getItem('tasks')||"[]")
  const deletee = () => 
  {
    let result = confirm("Are you sure to delete?");
    if(result)
    {
        savedTasks = savedTasks.filter((e) => e.taskName !== this.parentNode.firstChild.innerHTML);
        localStorage.setItem("tasks", JSON.stringify(savedTasks));
        savedTasks = savedTasks.filter((e) => e.assignee !== this.parentNode.lastChild.innerHTML);
        localStorage.setItem("tasks", JSON.stringify(savedTasks));
        this.parentNode.remove();
        count = document.getElementsByTagName("div").length;
        document.getElementById("TD_count").innerHTML = "To-DO:" + count;
        countd = savedTasks.filter(value => value.done === true).length;
        document.getElementById("D_count").innerHTML = "Done:" + countd;
    }

  };

  let ListItems=alltasks.map((d) => {
    return(
      <section className="my-tasks">
        <div key ={d.task} >
          <p>{d.task}</p>
          <button className="done" >Add Task</button>
          <button className='delete' onClick={deletee}>Delete Task</button>
          <p>{d.assignee}</p>
        </div>
      </section>)
 });

  const savedata = () => {
    let mytask = {
      task : task,
      assignee : assignee ,
      done : false
    }
    alltasks.push(mytask)
    localStorage.setItem('tasks',JSON.stringify(alltasks))
      }
      return (
        <div>
          <form id="form">
            <label htmlFor="taskname">Task Name: 
            <input  required name="taskname" id="taskname" value={task} onChange={(e)=>(settask(e.target.value))} type="text" placeholder="text" />
                </label>
                <label htmlFor="assignee">Assignee : 
            <input  required name="assignee" id="assignee" value= {assignee} onChange={(e)=>(setassignee(e.target.value))} type="text" placeholder="text" />
            <button id="AddB" className="b_add" onClick={savedata} >Add Task</button>

                </label>
            </form>
          <hr/>
          <Search />
          {ListItems}
          <End />

        </div> 
      );}

export default App;
