import React , {useState} from 'react';
 import Child from './Child';
 import './index.css';
 class App extends React.Component {

  hadleclick = (e) =>{
    <Child task = {e.tavalue} />
  }
    render(){
      /*return (
        <div>
          <form id="form">
            <label htmlFor="taskname">Task Name: 
            <input required name="taskname" id="taskname" value="" type="text" placeholder="text" />
                </label>
                <label htmlFor="assignee">Assignee : 
                    <input required name="assignee" id="assignee" value="" type="text" placeholder="text" />
                    <button id="AddB" className="b_add" >Add Task</button>
                </label>
            </form>
          <hr/>
        </div> // container end 
      )*/

   const [task, setName] = useState('');
   const [assignee, setPwd] = useState('');

   const handle = () => {
      localStorage.setItem('Task', task);
      localStorage.setItem('assignee', assignee);
   };
   return (
   <div>
         <input
            placeholder="Name"
            value={task}
            onChange={(e) => setName(e.target.value)}
         />
         <input
            type="text"
            value={assignee}
            onChange={(e) => setPwd(e.target.value)}
         />
         <div>
            <button onClick={handle}>Done</button>
         </div>
         {localStorage.getItem('Name') && (
            <div>
               Name: <p>{localStorage.getItem('Name')}</p>
            </div>
         )}
         {localStorage.getItem('Password') && (
            <div>
               Password: <p>{localStorage.getItem('Password')}</p>
            </div>
         )}
      </div>
   );
};
 }
export default App;
