import React, { useEffect, useState } from 'react';
function End({alltasks})
{
    const [Done,setDone]=useState(0)
    useEffect(()=>{
        Done
    },[Done])
    return(
        <footer className="counts" id="counts">
        <p id="TD_count">To-Do:{alltasks.length}</p>
        <p id="D_count">Done:{alltasks.filter(value => value.done === true).length}</p>
        </footer>
    );
}
export default End;
