import React , {useState } from 'react';
import Search from './Search';
import End from './End';
//import {ButtonComponent} from '@syncfusion/ej2-react-buttons';
 import './index.css';

 function App (props)
 {
  const[task,settask] = useState('')
  const[assignee,setassignee] = useState('')
  let alltasks = JSON.parse(localStorage.getItem('tasks')||"[]")

  let ListItems=alltasks.map((todo) => {
    return(
        <div key ={todo.task} >
          <p>{todo.task}</p>
          <button className="done"  onClick={() => doneTodo(todo.id)}>Done </button>
          <button className='delete' onClick={() => deleteTodo(todo.id)}>Delete</button>
          <p>{todo.assignee}</p>
        </div>
    )
 });

  const deleteTodo = (id) => 
  {
    let result = window.confirm("Are you sure to delete?");
    if(result)
    {
       // Filter out todo with the id
       alltasks = alltasks.filter((todo) => todo.id !== id);
       localStorage.setItem('tasks',JSON.stringify(alltasks));
    }
  };
  const handleClick = event => {
    // 👇️ toggle styles on click
    if (event.currentTarget.style.backgroundColor)
    {
      event.currentTarget.style.backgroundColor = null;
      event.currentTarget.style.color = null;
    } else {
      event.currentTarget.style.backgroundColor = 'salmon';
      event.currentTarget.style.color = 'white';
    }
  };
  const doneTodo = (id) => 
  {
   //Modify elements
   alltasks=alltasks.map(obj =>{if (obj.id === id ) {return {...obj, done: true};}return obj;});
   localStorage.setItem("tasks", (JSON.stringify(alltasks))); 
  };

 const savedata = () => {
  let mytask = {
    id: Math.random(),
    task : task,
    assignee : assignee ,
    done : false
  }
  alltasks.push(mytask)
  localStorage.setItem('tasks',JSON.stringify(alltasks))
  // clear input box
  settask("");
  setassignee("");
    };
      return (
        <div>
          <form id="form">
            <label htmlFor="taskname">Task Name: 
            <input  required name="taskname" id="taskname" value={task} onChange={(e)=>(settask(e.target.value))} type="text" placeholder="text" />
                </label>
                <label htmlFor="assignee">Assignee : 
            <input  required name="assignee" id="assignee" value= {assignee} onChange={(e)=>(setassignee(e.target.value))} type="text" placeholder="text" />
            <button className="b_add" onClick={savedata} >Add Task</button>
                </label>
            </form>
          <hr/>
          <Search />
          <section className="my-tasks">
          {ListItems}
          </section>
          <End />
        </div> 
      );}

export default App;
