import React from 'react';
class Child extends React.Component
{
    render()
    {
      const {alltasks}= this.props.alltasks;
      const thetasks = alltasks.map((item)=> {
        return(
          <div>
            <p>{item.task}</p>
            <p>{item.assignee}</p>
          </div>
          
        )
      })
      return (
        <section id="my-tasks">
          <div>
           {thetasks}
          </div>
        </section>
      )
    }
}
export default Child;
