import React , {useState , useEffect}from 'react';
import './index.css';
 function App ()
 {
  const[assignee,setAssignee] = useState("")
  const [task, setTask] = useState("");
  const [todos, setTodos] = useState([]);

  useEffect(()=>{
      if(localStorage.getItem("todos"))
      {
          const storedTodos = JSON.parse(localStorage.getItem("todos"));
          setTodos(storedTodos);
      }
  },[])

  const addTodo = (e) => {
    if (task && assignee ) {
      const newTask ={
        id: Math.random() , 
        task : task,
        assignee:assignee , 
        done:false
      };

      setTodos([...todos, newTask]);
      localStorage.setItem("todos", JSON.stringify([...todos, newTask]));
      setTask("");
      setAssignee("");
    }
  };

  const Clear=()=>{
    setTodos([]);
    localStorage.removeItem("todos");
}


////search
    const [searchText, setSearchText] = useState("");
    const Readtasks = todos.filter(
        ({ task, assignee }) =>
          task.toLowerCase().includes(searchText.toLowerCase()) ||
          assignee.toLowerCase().includes(searchText.toLowerCase())
      );
      const deleteTodo = id => 
  {
    let result = window.confirm("Are you sure to delete?");
    if(result)
    {
       let newtasks = todos.filter((todo) => todo.id !== id);
       setTodos([...newtasks]);
    }
  };
  const doneTodo = (e,id )=> {
  if (e.currentTarget.style.backgroundColor) {
    e.currentTarget.style.backgroundColor = null;
    e.currentTarget.style.color = null;
    //alltasks=alltasks.map(obj =>{if (obj.id === id ) {return {...obj, done: false};}return obj;});
    //window.localStorage.setItem("tasks", (JSON.stringify(alltasks))); 
  } else {
    e.currentTarget.style.backgroundColor = 'green';
    e.currentTarget.style.color = 'white';
    //alltasks=alltasks.map(obj =>{if (obj.id === id ) {return {...obj, done: true};}return obj;});
   //window.localStorage.setItem("tasks", (JSON.stringify(alltasks))); 
  }
  };

  let ListItems = Readtasks.map((todo) => {
    return(
        <div key ={todo.id} >
          <p>Task Name: {todo.task}</p>
          <button className="done btn_class"  onClick={(e) => doneTodo(e,todo.id)}>Done </button>
          <button className='delete' onClick={() => deleteTodo(todo.id)}>Delete</button>
          <p>Assignee: {todo.assignee}</p>
        </div>
    )
 });

      return (
        <div>
          <form id="form">
            <label htmlFor="taskname">Task Name: 
            <input  required name="taskname" id="taskname" value={task} onChange={(e)=>(setTask(e.target.value))} type="text" placeholder="task name" />
                </label>
                <label htmlFor="assignee">Assignee : 
            <input  required name="assignee" id="assignee" value= {assignee} onChange={(e)=>(setAssignee(e.target.value))} type="text" placeholder="assignee" />
            <button className="b_add" onClick={addTodo} >Add Task</button>
                </label>
            </form>
          <hr/>
          <div className="search">
        <input
          type="text"
          value={searchText}
          id="search" placeholder=" Search"
          onChange={({ target }) => setSearchText(target.value)}
        />
          <div className="my-tasks">
          {ListItems}
          </div></div>
          <footer className="counts" id="counts">
        <p id="TD_count">To-Do:{todos.length}</p>
        <p id="D_count">Done:{todos.filter(value => value.done === true).length}</p>
        <button className="clear" onClick={()=>Clear()} >Clear </button>
        </footer>
        </div> 
      );}
export default App;
