import React , {useState , useEffect}from 'react';
//import End from './End';
//import Search from './search';
//import Form from './Form';
import './index.css';
 function App (props)
 {
  const[task,settask] = useState('')
  const[assignee,setassignee] = useState('')
  let alltasks = JSON.parse(window.localStorage.getItem('tasks')||"[]")

  const savedata = () => {
      let mytask = {
    id: Math.random(),
    task : task,
    assignee : assignee ,
    done : false
  }
  alltasks.push(mytask)
 // window.localStorage.setItem('tasks',JSON.stringify(alltasks))
  // clear input box
  settask("");
  setassignee("");
    };
    useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(alltasks));
  });
    const [searchText, setSearchText] = useState("");

    const filteredtasks = alltasks.filter(
        ({ task, assignee }) =>
          task.toLowerCase().includes(searchText.toLowerCase()) ||
          assignee.toLowerCase().includes(searchText.toLowerCase())
      );
      const deleteTodo = (id) => 
  {
    let result = window.confirm("Are you sure to delete?");
    if(result)
    {
       // Filter out todo with the id
       alltasks = alltasks.filter((todo) => todo.id !== id);
       localStorage.setItem('tasks',JSON.stringify(alltasks));
       set(appear())
    }
  };
  const doneTodo = (id) => 
  {
   //Modify elements
   alltasks=alltasks.map(obj =>{if (obj.id === id ) {return {...obj, done: true};}return obj;});
   window.localStorage.setItem("tasks", (JSON.stringify(alltasks))); 
  };

  let ListItems = filteredtasks.map((todo) => {
    return(
        <div key ={todo.id} >
          <p>{todo.task}</p>
          <button className="done"  onClick={() => doneTodo(todo.id)}>Done </button>
          <button className='delete' onClick={() => deleteTodo(todo.id)}>Delete</button>
          <p>{todo.assignee}</p>
        </div>
    )
 });
      return (
        <div>
          <form id="form">
            <label htmlFor="taskname">Task Name: 
            <input  required name="taskname" id="taskname" value={task} onChange={(e)=>(settask(e.target.value))} type="text" placeholder="text" />
                </label>
                <label htmlFor="assignee">Assignee : 
            <input  required name="assignee" id="assignee" value= {assignee} onChange={(e)=>(setassignee(e.target.value))} type="text" placeholder="text" />
            <button className="b_add" onClick={savedata} >Add Task</button>
                </label>
            </form>
          <hr/>
          <div className="search">
        <input
          type="text"
          value={searchText}
          id="search" placeholder=" Search"
          onChange={({ target }) => setSearchText(target.value)}
        />
          <div className="my-tasks">
          {ListItems}
          </div></div>
          <footer className="counts" id="counts">
        <p id="TD_count">To-Do:{alltasks.length}</p>
        <p id="D_count">Done:{alltasks.filter(value => value.done === true).length}</p>
        </footer>
        </div> 
      );}
export default App;
