import React , {useState , useRef} from 'react';
import Search from './Search';
import End from './End';
//import {ButtonComponent} from '@syncfusion/ej2-react-buttons';

 import './index.css';

 function App (props)
 {
  const[task,settask] = useState('')
  const[assignee,setassignee] = useState('')

  let alltasks = JSON.parse(localStorage.getItem('tasks')||"[]")
  const deleteTodo = (id) => 
  {
    let result = window.confirm("Are you sure to delete?");
    if(result)
    {
       // Filter out todo with the id
       alltasks = alltasks.filter((todo) => todo.id !== id);
       localStorage.setItem('tasks',JSON.stringify(alltasks));

    }

  };
  const donee = (id) => 
  {
    if (this.style.color != "blue")
    {
                        this.style.color="blue";
                        this.parentNode.style.borderColor = "blue";
                        //Modify elements
                        alltasks=alltasks.map(obj =>{if (obj.id === id ) {return {...obj, done: true};}return obj;});
                        localStorage.setItem("tasks", (JSON.stringify(alltasks)));
                    }
                    else
                    {
                        this.style.color="black";
                        this.parentNode.style.borderColor = "white";
                        savedTasks=savedTasks.map(obj =>{if (obj.taskName === this.parentNode.firstChild.innerHTML ) {return {...obj, done: false};}return obj;});
                        localStorage.setItem("tasks", (JSON.stringify(savedTasks)));
                    }
  };

  let ListItems=alltasks.map((d) => {
    return(
        <div key ={d.task} >
          <p>{d.task}</p>
          <button className="done"onClick={donee} >done </button>
          <button className='delete' onClick={() => deleteTodo(d.id)}>&times;</button>
          <p>{d.assignee}</p>
        </div>
    )
 });
  const savedata = () => {
    let mytask = {
      id: Math.random(),
      task : task,
      assignee : assignee ,
      done : false
    }
    alltasks.push(mytask)
    localStorage.setItem('tasks',JSON.stringify(alltasks))
      };
      return (
        <div>
          <form id="form">
            <label htmlFor="taskname">Task Name: 
            <input  required name="taskname" id="taskname" value={task} onChange={(e)=>(settask(e.target.value))} type="text" placeholder="text" />
                </label>
                <label htmlFor="assignee">Assignee : 
            <input  required name="assignee" id="assignee" value= {assignee} onChange={(e)=>(setassignee(e.target.value))} type="text" placeholder="text" />
            <button id="AddB" className="b_add" onClick={savedata} >Add Task</button>

                </label>
            </form>
          <hr/>
          <Search />
          <section className="my-tasks">{ListItems}</section>
          <End />

        </div> 
      );}

export default App;
